<script>
document.addEventListener('DOMContentLoaded', function() {
    const carousel = document.querySelector('.cert-carousel');
    const slides = carousel.querySelector('.flex');
    const prevBtn = document.querySelector('.cert-carousel-prev');
    const nextBtn = document.querySelector('.cert-carousel-next');

    let currentIndex = 0;
    const slideCount = document.querySelectorAll('.flex-shrink-0').length;
    const visibleSlides = window.innerWidth >= 1024 ? 3 : window.innerWidth >= 640 ? 2 : 1;

    function updateCarousel() {
        const slideWidth = 100 / visibleSlides;
        slides.style.transform = `translateX(-${currentIndex * slideWidth}%)`;
    }

    prevBtn.addEventListener('click', () => {
        currentIndex = Math.max(0, currentIndex - 1);
        updateCarousel();
    });

    nextBtn.addEventListener('click', () => {
        currentIndex = Math.min(slideCount - visibleSlides, currentIndex + 1);
        updateCarousel();
    });

    // Auto-slide every 5 seconds
    let autoSlide = setInterval(() => {
        if (currentIndex >= slideCount - visibleSlides) {
            currentIndex = 0;
        } else {
            currentIndex++;
        }
        updateCarousel();
    }, 5000);

    // Pause auto-slide on hover
    carousel.addEventListener('mouseenter', () => {
        clearInterval(autoSlide);
    });

    carousel.addEventListener('mouseleave', () => {
        autoSlide = setInterval(() => {
            if (currentIndex >= slideCount - visibleSlides) {
                currentIndex = 0;
            } else {
                currentIndex++;
            }
            updateCarousel();
        }, 5000);
    });

    // Responsive adjustments
    window.addEventListener('resize', () => {
        const newVisibleSlides = window.innerWidth >= 1024 ? 3 : window.innerWidth >= 640 ? 2 : 1;
        if (newVisibleSlides !== visibleSlides) {
            currentIndex = 0;
            updateCarousel();
        }
    });

    // Initialize
    updateCarousel();
});
</script>

<style>
.cert-carousel {
    width: 100%;
    overflow: hidden;
    position: relative;
}

.cert-carousel .flex {
    display: flex;
    width: 100%;
}

.cert-carousel .flex-shrink-0 {
    flex: 0 0 auto;
    transition: transform 0.5s ease-in-out;
}

@media (max-width: 639px) {
    .cert-carousel .flex-shrink-0 {
        width: 100%;
    }
}

@media (min-width: 640px) and (max-width: 1023px) {
    .cert-carousel .flex-shrink-0 {
        width: 50%;
    }
}

@media (min-width: 1024px) {
    .cert-carousel .flex-shrink-0 {
        width: 33.3333%;
    }
}
</style>
